#include &quot;game.h&quot;//初始化数组数组全部为空格void InitBoard(char board[ROW][COL], int row, int col)&#123;    int i = 0;    int j = 0;    for (i = 0; i &lt; row; i ++)    &#123;        for (j = 0; j &lt; col; j ++)        &#123;            board[i][j] = &#x27; &#x27;;        &#125;    &#125;&#125;//打印棋盘void Displayer(char board[ROW][COL], int row, int col)&#123;    int i = 0;    for (i = 0; i &lt; row; i ++)    &#123;        int j = 0;        for (j = 0; j &lt; col; j ++)        &#123;            printf(&quot; %c &quot;, board[i][j]);            if (j &lt; col - 1)                printf(&quot;|&quot;);        &#125;        printf(&quot;
&quot;);        if (i &lt; row - 1)        &#123;            int x = 0;            for (x = 0; x &lt; col; x ++)            &#123;                printf(&quot;---&quot;);                if (x &lt; col - 1)                    printf(&quot;|&quot;);            &#125;            printf(&quot;
&quot;);        &#125;    &#125;&#125;//玩家下棋的实现void playermove(char board[ROW][COL], int row, int col)&#123;    int x = 0;     int y = 0;    printf(&quot;玩家请下棋
&quot;);    printf(&quot;请输入坐标 :&gt;&quot;);    while(1)    &#123;        scanf(&quot;%d %d&quot;, &amp;x, &amp;y);        //坐标范围合法的判断        if (x &gt;= 1 &amp;&amp; x &lt;= row &amp;&amp; y &gt;= 1 &amp;&amp; y &lt;= col)        &#123;            if (board[x - 1][y - 1] == &#x27; &#x27;)            &#123;                board[x - 1][y - 1] = &#x27;*&#x27;;                break;            &#125;            else            &#123;                printf(&quot;该位置已经被占用，请重新下棋:&gt;&quot;);            &#125;        &#125;        else        &#123;            printf(&quot;你输入的坐标错误，请重新输入:&gt;&quot;);        &#125;    &#125;&#125;//电脑下棋void computermove(char board[ROW][COL], int row, int col)&#123;    printf(&quot;电脑下棋
&quot;);    int x = 0;    int y = 0;        while (1)    &#123;        x = rand() % row;        y = rand() % col;//随机生成数在 1 ～ 2范围内        if (board[x][y] == &#x27; &#x27;)        &#123;            board[x][y] = &#x27;#&#x27;;            break;        &#125;    &#125;&#125;//判断是否为平局int isFull(char board[ROW][COL], int row, int col)&#123;    int i = 0;    int j = 0;    for (i = 0; i &lt; row; i ++)    &#123;        for (j = 0; j &lt; col; j ++)        &#123;            if (board[i][j] == &#x27; &#x27;)            &#123;                return 0;            &#125;          &#125;    &#125;    return 1;&#125;//判断输赢char Iswin(char board[ROW][COL], int row, int col)&#123;    //行    int i = 0;    int j = 0;    for (i = 0; i &lt; row; i ++)    &#123;        if (board[i][0] == board[i][1] &amp;&amp; board[i][1] == board[i][2] &amp;&amp; board[i][0] != &#x27; &#x27;)        &#123;            return board[i][1];        &#125;    &#125;    //列    for (j = 0; j &lt; col; j ++)    &#123;        if (board[0][j] == board[1][j] &amp;&amp; board[1][j] == board[2][j] &amp;&amp; board[1][j] != &#x27; &#x27;)        &#123;            return board[1][j];        &#125;    &#125;    //对角线    if (board[0][0] == board[1][1]&amp;&amp; board[1][1] == board[2][2] &amp;&amp; board[1][1] != &#x27; &#x27;)    &#123;        return board[1][1];    &#125;    else if (board[0][2] == board[1][1]&amp;&amp; board[1][1] == board[2][0] &amp;&amp; board[1][1] != &#x27; &#x27;)    &#123;        return board[1][1];    &#125;    //没人赢，判断平局    if (isFull(board, row, col) == 1)    &#123;        return &#x27;Q&#x27;;    &#125;    else        return &#x27;C&#x27;;&#125;
